import jxl.*
//if i want to know the path of my excel sheet
def email,firstname,lastname
filePath = "C:/Users/PrathvirajMeenkeri/Desktop/Prathvi/IBM Training/API Middleware IBM Upskill/Project/Assignment_Projects/SOAP_Project/TestData.xls"

//am putting my file in a variable called myfile
File myFile = new File(filePath)

//Excel contains Workbook and sheet
Workbook wb = Workbook.getWorkbook(myFile)
// i want to fetch the sheet
Sheet sh=wb.getSheet("CR")

//log.info sh.getCell(0,0).getContents();
//log.info sh.getCell(0,1).getContents();

row=sh.getRows().toInteger()
column=sh.getColumns().toInteger()
//i want to print number of columns and rows
log.info "The number of rows are $row and columns are $column "

for(def i=1;i<row;i++)
{
	for(def j=0;j<column;j++)
	{
		//celldata = sh.getCell(j,i).getContents()	
		//log.info celldata
		if(j==0)
		{
			email = sh.getCell(j,i).getContents()
		}
		else if(j==1)
		{
			firstname = sh.getCell(j,i).getContents()
		}
		else
		{	
			lastname = sh.getCell(j,i).getContents()
		}
	}
def tc = testRunner.testCase.testSuite.project.testSuites["CCR_Functional_Testing"].testCases["Customer_Repository"]
