import com.eviware.soapui.support.XmlHolder
def jdbcresp = context.testCase.testSteps["JDBCRequest"].getPropertyValue("responseAsXml")
//log.info jdbcresp
def n
def holder=new XmlHolder(jdbcresp)
def Rowcount = holder.getNodeValue("count(//ResultSet/Row/CUSTOMER.EMAIL)")
def count = Rowcount.toInteger()
//log.info count
for(def i=1;i<=count;i++)
{
	def email = holder.getNodeValue("//ResultSet/Row["+i+"]/CUSTOMER.EMAIL")
	def firstname = holder.getNodeValue("//ResultSet/Row["+i+"]/CUSTOMER.FIRSTNAME")
	def lastname = holder.getNodeValue("//ResultSet/Row["+i+"]/CUSTOMER.LASTNAME")
	log.info "Customer Details are Email -> $email , FirstName -> $firstname, LastName ->$lastname"

