import com.eviware.soapui.support.XmlHolder

def Expected_CreateOrder_Duplicate = "env:Sender"
def CreateOrder_Duplicate_Response = context.expand('${CCR_CreateOrder_Duplicate#Response#//*:Value}')
log.info CreateOrder_Duplicate_Response
assert Expected_CreateOrder_Duplicate == CreateOrder_Duplicate_Response

def Text = context.expand('${CCR_CreateOrder_Duplicate#Response#//*:Text}')
log.info Text
if(Text.contains("A customer with the same email address already exists in an associated website.</env:Text>"))
{
	log.info true
}
else
{
	log.info false
}

