log.info count
for(def i=0;i<=count;i++)
{
	def username = holder.getNodeValue("//ResultSet/Row["+i+"]/LOGIN.NAME")
	def password = holder.getNodeValue("//ResultSet/Row["+i+"]/LOGIN.PASSWORD")
	log.info username
	log.info password
